#!/usr/bin/env node
/*jshint node:true */
'use strict';

var fs = require('fs');
var readme = fs.readFileSync('../README.md','utf8');

var params = JSON.parse(fs.readFileSync('./params.json','utf8'));
params.body = readme.replace(/\n/g,"\r\n");

fs.writeFileSync('./params.json', JSON.stringify(params));
